    //     });
    // }
  };

  let inputProps = {
    file,
    handleClick,
  };

  switch (file.attributes.type) {
    case "folder":
      return (
        <Grid
          onClick={() => {
            handleClick(
              file,
              state?.settingData?.[settingId]?.previousData?.[file?.id]
            );
          }}
          {...inputProps}
          id={file.id}
          draggable="true"
          onDragStart={(e) => {
            console.log({ folder: e });

            e.dataTransfer.setData("text", ev.target.id);
            // e.dataTransfer.setData(
            //   "fileSize",
            //   file.file.attributes.storage_info.size
            // );
            // e.dataTransfer.setData(
