import React, { useEffect, useState } from "react";
import axios from "axios";
// const base_url = `http://192.168.0.107:3005/api/v1/storage/workdrive/folder_id/files/bz58aa141943840e948fe852d39d8eb9712b8/thumbnail`;
const base_url = `https://previewengine-accl.zoho.com/thumbnail/WD/bz58aa141943840e948fe852d39d8eb9712b8`;

function CustomPhoto(props) {
  const [png, setPng] = useState("");
  const [base64, setbase64] = useState("");
  useEffect(() => {
    axios({
      method: "get",
      headers: {
        Authorization:
          "Bearer 1000.12483c287346c6ce4487318390a224ef.bf032fc079333370f599151fc4ff9970",
      },
        responseType: "blob",
      url: base_url,
    })
      .then((response) => {
        // setPng(response.data);
        var blodData = new Blob([response.data]);
        var urlData = URL.createObjectURL(blodData);
        console.log({ response: response.data, urlData });
        // setbase64(urlData);
        // return response.data;
      })
      .catch((error) => {
        console.log({ error: error });
      });
  }, []);
  return (
    <div>
      <div>{png}</div>
      {/* <div>{png}</div> */}
      {/* <div> {base64} </div> */}
      {/* <img src={`data:image/png;;base64,${base64}`} alt="Image" /> */}
      <img
        src="blob:http://localhost:3000/2fdcf2f2-b03a-4aa8-85af-6b9457c8b401"
        alt="Image"
      />
      <img width="100%" alt="Image" src={base64} />
    </div>
  );
}

export default CustomPhoto;
