import React, { useEffect, useState } from "react";
import "react-perfect-scrollbar/dist/css/styles.css";
import { BuildTheme } from "./assets/global/Theme-variable";
import Response from "./components/Playground/Response";
import "./App.css";

import { makeStyles } from "@mui/styles";

import Backdrop from "@mui/material/Backdrop";
import CircularProgress from "@mui/material/CircularProgress";
import useTrackedStore from "./store/useTrackedStore";
import * as ApiCall from "./components/Playground/api/ApiCalling";

const ZOHO = window.ZOHO;
const useStyles = makeStyles({
  grid: {
    border: "1px solid black",
  },
  icon: {
    fontSize: "30px !important",
  },
});

const App = () => {
  const classes = useStyles();
  const state = useTrackedStore();
  const [entity, setEntity] = useState("");
  const [entityId, setEntityId] = useState("");
  const [zohoLoaded, setZohoLoaded] = useState(false)
  useEffect(() => {
    /*
     * Subscribe to the EmbeddedApp onPageLoad event before initializing
     */

    ZOHO.embeddedApp.on("PageLoad", function (data) {
      console.log("Module Data ", data);
      setEntity(data?.Entity);
      setEntityId(data?.EntityId)
      setZohoLoaded(true);
      //Custom Bussiness logic goes here
    });

    /*
     * initializing the widget.
     */
    ZOHO.embeddedApp.init();
  }, []);

  useEffect(async ()=> {
    if(zohoLoaded && entity !== "" && entityId !== ""){
      const settings = await  ZOHO.CRM.API.searchRecord({Entity:"zworkflows__ZWorkFlow_Settings",Type:"criteria",Query:"(((zworkflows__Service_Type:equals:'ZOHO Workdrive') and (zworkflows__Settings_Type:equals:Addon)) and (zworkflows__Module_Name:equals:"+entity+" ))"})
      const setting1 = settings?.data?.[0];
      const encSchema = setting1?.Front_End_Schema
      console.log({setting1})
    }
  }, [zohoLoaded, entity, entityId])

  // React.useEffect(() => {
  //   ApiCall.getAccessToken()
  //     .then((res) => {
  //       state.setToken(res.access_token);
  //       state.setLoading(false);
  //     })
  //     .catch((error) => {
  //       console.log(error);
  //     });
  // });

  const customizer = {
    direction: "ltr",
    theme: "PURPLE_THEME",
    activeMode: "light",
  };
  const theme = BuildTheme(customizer);
  return (
    <div className="App">
      {/* <Response /> */}
      {/* <Backdrop
        sx={{ color: "#fff", zIndex: (theme) => theme.zIndex.drawer + 1 }}
        open={state.loading}
      >
        <CircularProgress color="inherit" />
      </Backdrop> */}
    </div>
  );
};

export default App;
