  //     })
  //     .catch((error) => {
  //       console.log(error);
  //     });
  // }, [state.token]);

  useEffect(() => {
    setSearchVal("");
    if (!state.settingData?.[settingId]?.previousData?.[rootFolderId]) {
      ApiCall.getFoldersItem(userAccessToken, rootFolderId)
        .then((res) => {
          setPost(res.data);
          state.setApiData(rootFolderId, res.data);
          state.setApiSettingData(settingId, rootFolderId, res.data);
          state.setLoading(false);
        })
        .catch((error) => {
          console.log(error);
