    }

    // if (rootFolderId && state?.token) {

    //   ApiCall.getFoldersItem(state?.token, rootFolderId)
    //     .then((res) => {
    //       setPost(res.data);
    //       state.setApiData(rootFolderId, res.data);

    //       state.setLoading(false);
    //     })
    //     .catch((error) => {
    //       console.log(error);
    //     });
    // }
