    state.setId(data?.id);
    moveId.current = data?.id;
    // moveId.type = "move";
    console.log({ moveId });
  }

  function pasteData(e, data) {
    if (data?.attributes?.type === "folder" && data?.id !== moveId.current) {
      ApiCall.moveFile(state?.token, data, moveId.current)
        .then((res) => {
          let xArray = post.filter((file) => file.id != moveId.current);
          setSnackOpen(true);
          console.log({ xArray });
          setPost(xArray);
          state.setApiData(state.bread[state.bread.length - 1].id, xArray);
          console.log(res);
        })
        .catch((err) => console.log(err));
      console.log({ id: moveId.current });
    }
  }

  // const moveData = (e, data) => {
  //   console.log(data?.id);
  //   state.setPasteChildId(data?.id)
  // };

  // const pasteData = (e, data) => {
  //   console.log({id: state.pasteChildId});
  //   // console.log(data?.id);
  // };

  async function handleClick(file) {
    if (!state.apiData.hasOwnProperty(file?.id)) {
      console.log({ notFound: "id found handleClick" });
      Object.keys(state.apiData).map((i) => console.log(i));
      // console.log(file.id, ' handleClick');
      // console.log({notFound: 'id not found handleClick'});
      ApiCall.getFoldersItem(state?.token, file?.id)
        .then((res) => {
          state.setApiData(file.id, res.data);

          state.setApiSettingData(settingId, file, res.data);
          state.setBreadCrumbs(file);
          setPost(res.data);
          setSearchVal("");
        })
        .catch((error) => {
          console.log(error);
        });
    } else {
      Object.keys(state.apiData).map((i) => console.log(i));
      console.log({ idFound: "id found handleClick" });
      state.setBreadCrumbs(file);
      setPost(state.apiData[file.id]);
      setSearchVal("");
    }
  }

  async function setBreadCrumbsUrl(file) {
    if (!state.apiData.hasOwnProperty(file?.id)) {
      console.log({ notFound: "id not Found found setBreadCrumbsUrl" });
      try {
        const response = ApiCall.getFoldersItem(state?.token, file?.id);
        state.setBreadCrumbsUrl(file);
        state.setApiData(file.id, response.data);
        setPost(response.data.data);
        setSearchVal("");
      } catch (error) {
        console.log(error);
      }
    } else {
      Object.keys(state.apiData).map((i) => console.log(i));
      console.log({ idFound: "id found setBreadCrumbsUrl" });
      state.setBreadCrumbsUrl(file);
      state.setBreadCrumbsSettingData(file);
      setPost(state.apiData[file.id]);
      setSearchVal("");
    }
  }

  const [files, setFiles] = React.useState([]);
  const { getRootProps, getInputProps } = useDropzone({
    noClick: true,
    noKeyboard: true,
    // accept: "image/*",
    onDrop: (acceptedFiles) => {
      console.log("File type detection", acceptedFiles);
      setOpen(true);
      setFiles(
        acceptedFiles.map((file, index) => {
          if (file.type.split("/")[1] == "pdf")
            return Object.assign(file, {
              preview: URL.createObjectURL(file),
              special: true,
              specialType: "pdf",
              id: index,
            });
          else if (file.type.split("/")[0] == "image") {
            return Object.assign(file, {
              preview: URL.createObjectURL(file),
              special: false,
              specialType: "image",
              id: index,
            });
          } else if (file.type.split("/")[1] == "zip") {
            return Object.assign(file, {
              preview: URL.createObjectURL(file),
              special: false,
              specialType: "zip",
              id: index,
            });
          } else if (file.type.split("/")[1] == "msword") {
            return Object.assign(file, {
              preview: URL.createObjectURL(file),
              special: false,
              specialType: "msword",
              id: index,
            });
          } else {
            return Object.assign(file, {
              preview: URL.createObjectURL(file),
              special: false,
              specialType: "other",
              id: index,
            });
          }
        })
      );
    },
  });

  const removeFile = (deleteFile) => {
    console.log(deleteFile);
    setFiles(files.filter((file) => file.id != deleteFile.id));
  };

  const uploadFile = (folderId) => {
    // Do something with the files
    console.log(files);

    var myCustomArray = post;
    files.forEach(async (element, index) => {
      console.log({ FolderId: state.bread[state.bread.length - 1].id });
      const data = new FormData();
      data.append("file", element);
      // console.warn(element);
      console.log({
        formData: data,
        element,
      });
      // console.log({fileUploading: myUrl});
      try {
        const response = await ApiCall.fileUploader(
          state?.token,
          data,
          state.bread.slice(-1)[0].id
        );
        // console.log({ testPurpose: response });
        // console.log({
        //   dataTesting: response.data.data[0].attributes.resource_id,
        // });

        let myCustomFile = FileUploadResponse.makeCustomFile(response);
        myCustomArray.unshift(myCustomFile);
        console.log({ myCustomArray: myCustomArray });
        state.setApiData(state.bread[state.bread.length - 1].id, myCustomArray);
        setPost(myCustomArray);
        setSnackOpen(true);
        console.log({ post: post });
      } catch (error) {
        console.log({ error: error });
      }
    });
    setFiles([]);
  };

  const thumbs = files.length ? (
    <ModalFileUpload
      files={files}
      removeFile={removeFile}
      open={open}
      setOpen={setOpen}
      handleCloseModal={handleCloseModal}
      uploadFile={uploadFile}
    />
  ) : (
    <></>
  );

  useEffect(
    () => () => {
      // Make sure to revoke the data uris to avoid memory leaks
      files.forEach((file) => URL.revokeObjectURL(file.preview));
    },
    [files]
  );

  const handleCloseSnack = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }
    setSnackOpen(false);
  };

  return (
    <Grid
      container
      alignItems="center"
      justifyContent="center"
      {...getRootProps({ className: "dropzone disabled" })}
    >
      {/* <div>{JSON.stringify(post??[])}</div> */}
      {/* <div>
        <div>{
          state.bread.map(i => <div>{JSON.stringify(i)}</div>)
        }</div>
        <br />
        <div>{
          Object.keys(state.apiData).map(i => <div>{JSON.stringify(i)}</div>)
        }</div>
        <br />
        <div>{
          state.bread[state.bread.length - 1].id
        }</div></div> */}

      <Box sx={{ display: "none" }}>
        <div>
          {state.bread.map((i) => (
            <div>{JSON.stringify(i)}</div>
          ))}
        </div>
        <br />
        <div>
          {Object.keys(state.apiData).map((i) => (
            <div>{JSON.stringify(i)}</div>
          ))}
        </div>
        <br />
        <div>{state.bread[state.bread.length - 1].id}</div>
      </Box>
      <Grid item container>
        <CustomSeparator
          settingId={settingId}
          setBreadCrumbsUrl={setBreadCrumbsUrl}
          searchVal={searchVal}
          setSearchVal={setSearchVal}
          handleClickOpen={handleClickOpen}
          setPost={setPost}
          post={post}
          setSnackOpen={setSnackOpen}
        />
      </Grid>
      <input {...getInputProps()} />
      <div style={thumbsContainer}>{thumbs}</div>

      {filteredData.length === 0 && !state.loading && (
        <Grid item fullheight justifyContent="center">
          <Typography fullwidth variant="h1" component="div">
            Nothing here!!!
          </Typography>
        </Grid>
      )}
      {/* <div>{JSON.stringify(post??[])}</div> */}
      {filteredData.length !== 0 && (
        <Grid
          container
          item
          xs={12}
          spacing={{ xs: 2, md: 3 }}
          sx={{ padding: 5 }}
        >
          <Grid item xs={12} spacing={2}>
            {filteredData.filter((file) => file.attributes.type === "folder")
              .length !== 0 && (
              <Typography
                variant="h5"
                component="div"
                gutterBottom
                sx={{ textAlign: "left" }}
              >
                Folder
              </Typography>
            )}

            <Grid
              container
              spacing={{ xs: 2, md: 3 }}
              // columns={{ xs: 4, sm: 8, md: 12 }}
            >
              {filteredData?.map((file, index) => {
                if (file.attributes.type == "folder")
                  return (
                    <Grid item xs={12} sm={4} md={3}>
                      <CommonComponent
                        file={file}
                        handleClick={() => handleClick(file)}
                        setPost={setPost}
                        post={post}
                        setSnackOpen={setSnackOpen}
                        moveData={moveData}
                        pasteData={pasteData}
                      />
                    </Grid>
                  );
              })}
            </Grid>
          </Grid>
          <Grid item xs={12}>
            {filteredData.filter((file) => file.attributes.type !== "folder")
              .length !== 0 && (
              <Typography
                variant="h5"
                component="div"
                gutterBottom
                sx={{ textAlign: "left" }}
              >
                Files
              </Typography>
            )}

            {state.listView ? (
              <Grid
                container
                spacing={true ? { xs: 2, md: 4 } : {}}
                rowSpacing={false ? 0 : 1}
                columnSpacing={false ? {} : { xs: 1, sm: 2, md: 3 }}
              >
                {filteredData?.map((file, index) => {
                  if (file.attributes.type !== "folder")
                    return (
                      <Grid item xs={12} sm={6} md={4}>
                        <CommonComponent
                          file={file}
                          handleClick={() => handleClick(file)}
                          setPost={setPost}
                          post={post}
                          setSnackOpen={setSnackOpen}
                          moveData={moveData}
                          pasteData={pasteData}
                        />
                      </Grid>
                    );
                })}
              </Grid>
            ) : (
              <Grid
                container
                spacing={state.listView ? { xs: 2, md: 3 } : {}}
                columns={state.listView ? { xs: 4, sm: 8, md: 12 } : {}}
                rowSpacing={state.listView ? 0 : 1}
                columnSpacing={state.listView ? {} : { xs: 1, sm: 2, md: 3 }}
              >
                {filteredData?.map((file, index) => {
                  if (file.attributes.type !== "folder")
                    return (
                      <Grid item xs={12} md={3}>
                        <CommonComponent
                          file={file}
                          handleClick={() => handleClick(file)}
                          setPost={setPost}
                          post={post}
                          setSnackOpen={setSnackOpen}
                          moveData={moveData}
                          pasteData={pasteData}
                        />
                      </Grid>
                    );
                })}
              </Grid>
            )}
          </Grid>
        </Grid>
      )}
      <SnackAlert
        snackOpen={snackOpen}
        handleCloseSnack={handleCloseSnack}
        severity={"success"}
        message={"This is a success message!"}
      />
    </Grid>
  );
}

export default Response;
