import axios from "axios";

// const base_url = `https://workdrive.zoho.com/api`;
const base_url = `http://192.168.0.107:3005/api/v1/storage/workdrive`;

export const getAccessToken = () => {
  const URL =
    "https://www.zohoapis.com/crm/v2/functions/getaccesstoken/actions/execute";

  return axios(URL, {
    method: "GET",
    headers: {
      "content-type": "application/json", // whatever you want
    },
    params: {
      auth_type: "apikey",
      zapikey:
        "1003.3fda4e581ecb2838b2d8959665591777.90fcd8129f39eecb49682e34a3286ae0",
    },
  })
    .then((response) => response.data)
    .catch((error) => {
      throw error;
    });
};

export const getFoldersItem = (token, folder_id) => {
  const URL = `${base_url}/${folder_id}/files`;

  return axios(URL, {
    method: "GET",
    headers: {
      Authorization: token,
      Accept: "application/vnd.api+json",
    },
  })
    .then((response) => response.data)
    .catch((error) => {
      throw error;
    });
};

export const fileUploader = (token, data, parentId) => {
  const URL = `${base_url}/v1/upload?parent_id=${parentId}&override-name-exist=true`;

  return axios
    .post(URL, data, {
      headers: {
        Authorization: token,
        Accept: "application/vnd.api+json",
      },
    })
    .then((response) => response)
    .catch((error) => {
      console.log({ throwError: error });
      // throw error;
    });
};
// /:folder_id/files
// /:folder_id/files/:file_id
export const renameFileFolder = (token, data, file) => {
  // const URL = `${base_url}/v1/files/${file.id}`;
  const URL = `${base_url}/ohvjc910552a9c20445469a6ea2c15140bde5/files`;

  // return axios
  //   .patch(
  //     URL,
  //     {
  //       data: {
  //         attributes: {
  //           name: file.attributes.extn
  //             ? `${data.name}.${file.attributes.extn}`
  //             : data.name,
  //         },
  //         type: "files",
  //       },
  //     },
  //     {
  //       headers: {
  //         Authorization: token,
  //         Accept: "application/vnd.api+json",
  //       },
  //     }
  //   )
  //   .then((response) => response)
  //   .catch((error) => {
  //     console.log({ throwError: error });
  //     // throw error;
  //   });

  return axios
    .put(
      URL,
      {
        data: {
          name: file.attributes.extn
            ? `${data.name}.${file.attributes.extn}`
            : data.name,
          // attributes: {
          //   name: file.attributes.extn
          //     ? `${data.name}.${file.attributes.extn}`
          //     : data.name,
          // },
          // type: "files",
        },
      },
      {
        headers: {
          Authorization: token,
          Accept: "application/vnd.api+json",
        },
      }
    )
    .then((response) => response)
    .catch((error) => {
      console.log({ throwError: error });
      // throw error;
    });
};

export const deleteFileFolder = (token, file) => {
  const URL = `${base_url}/v1/files`;

  return axios
    .patch(
      URL,
      {
        data: [
          {
            id: file.id,
            type: "files",
            attributes: {
              status: 51,
            },
          },
        ],
      },
      {
        headers: {
          Authorization: token,
          Accept: "application/vnd.api+json",
        },
      }
    )
    .then((response) => response)
    .catch((error) => {
      console.log({ throwError: error });
      // throw error;
    });
};

export const moveFile = (token, file, childId) => {
  const URL = `${base_url}/v1/files`;

  return axios
    .patch(
      URL,
      {
        data: [
          {
            attributes: {
              parent_id: file.id,
            },
            id: childId,
            type: "files",
          },
        ],
      },
      {
        headers: {
          Authorization: token,
          Accept: "application/vnd.api+json",
        },
      }
    )
    .then((response) => response)
    .catch((error) => {
      console.log({ throwError: error });
      // throw error;
    });
};

export const getImageResponse = (token, file) => {
  const URL = `${base_url}/v1/download/${file?.id}`;

  return axios({
    method: "get",
    headers: {
      Accept: "application/vnd.api+json",
      Authorization: token,
    },
    responseType: "blob",
    url: URL,
  })
    .then((response) => response)
    .catch((error) => {
      console.log({ throwError: error });
      // throw error;
    });
};

export const getThumbnailData = (token, file) => {
  const URL = `https://previewengine-accl.zoho.com/thumbnail/WD/${file?.id}`;

  return axios({
    method: "get",
    headers: {
      Authorization: token,
    },
    responseType: "blob",
    url: URL,
  })
    .then((response) => response)
    .catch((error) => {
      console.log({ throwError: error });
      // throw error;
    });
};
