import React, { useEffect, useState } from "react";
import axios from "axios";
const base_url = `http://192.168.0.107:3005/api/v1/storage/workdrive/folder_id/files/bz58aa141943840e948fe852d39d8eb9712b8/thumbnail`;

function CustomPhoto(props) {
  const [png, setPng] = useState("");
  const [base64, setbase64] = useState("");
  useEffect(() => {
    axios({
      method: "get",
      headers: {
        Authorization: "Bearer sdkfjsdkf",
      },
      // responseType: "blob",
      url: base_url,
    })
      .then((response) => {
        let base64ImageString = Buffer.from(response.data, 'binary').toString('base64')
        const decoded = atob(response.data);
        console.log({
          response: response.data,
        });
        setPng(response.data);
        // return response.data;
      })
      .catch((error) => {
        console.log({ error: error });
        // throw error;
      });
  });
  return (
    <div>
      {/* <div>{png}</div> */}
      <div>{png}</div>
      <div>{base64} </div>
      <img src={png} alt="Image"/>
    </div>
  );
}

export default CustomPhoto;
